import React, { useState } from "react";

function App() {
  const [inputarr, setInputarr] = useState([]);
  const [inputdata, SetInputdata] = useState({
    name: " ",
    rollno: " ",
  });

  function changehandle(e) {
    SetInputdata({ ...inputdata, [e.target.value]: e.target.value });
  }

  let { name, rollno } = inputdata;
  function changehandl(e) {
    e.preventDefault();
    setInputarr([...inputarr, { name, rollno }]);
    console.log(inputarr);
    console.log(inputdata);
  }

  return (
    <div className="App">
      <form onChange={changehandle}>
        <input type="text" name="name" value={inputdata.name} />
        <br />
        <br />
        <input type="text" name="rollno" value={inputdata.rollno} />
        </form>
        <button onClick={changehandl}>add</button>
    </div>
  );
}

export default App;
