function App(){
    const[data,setdata]=useState(null)
    function getData(val){
        console.log(val.target.value)
        setdata(val.target.value)
    }


return (

    <div className="App">
    <h1>{data}</h1>
    <input type="text" onChange={getData}/>
    </div>
    )
}

export default App;


