         case   "dec" : 
         return state-1 

         case "res" :
            return 0;
             default :
                return state
    }
 }

const Usereduce = () => {
    const[count,dispatch]=useReducer(reducer,intialstate)

  return (
    <div>
<h1>{count}</h1>
 <button onClick={()=>(dispatch({type:"inc"}))}> +</button>
 <button onClick={()=>(dispatch({type:"dec"}))}> -</button>
 <button onClick={()=>(dispatch({type:"res"}))}> 0</button>
    </div>
  )
}

export default Usereduce
