import React, {useState,useReducer} from 'react'

const initialState = {
    data: [],
}
function reducer(state,action) {
    switch (action.type) {
        case 'ADD_DATA' :
        return{
            ...state,
            data: [...state.data, action.payload],           
        };
        default:
            return state;
    }
}

const App = () => {

  const [formData, setFormData] =useState(
    {firstname: '',
     lastname: '',
     age: '',
     email:'',
     phoneno:''});
 const [state,dispatch] = useReducer(reducer,initialState) ;

 const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch({ type: 'ADD_DATA', payload: formData });
    setFormData(
   {firstname: '',
    lastname: '',
    age: '',
    email:'',
    phoneno:''});
  };

  return (
    <div className='App'>
    <form onSubmit={handleSubmit}>
    <input 
    type="text"
    name="firstname"
    value={formData.fname}
    onChange={handleChange}
    placeholder="Firstname"
    /> <br/>
    <input 
    type="text"
    name="lastname"
    value={formData.lname}
    onChange={handleChange}
    placeholder="lastname"
    /> <br/>
    <input
          type="number"
          name="age"
          value={formData.age}
          onChange={handleChange}
          placeholder="Age"
        /> <br/>
        <input 
        type="text"
        name="email"
        value={formData.email}
        onChange={handleChange}
        placeholder="Email"
        />  <br/>
        <input 
        type="tel"
        name="phoneno"
        value={formData.phone}
        onChange={handleChange}
        placeholder="Phone.no"
        /> <br/>
        <button type="submit">Add</button>
    </form>

    <table>
    <thead>
    <tr>
    <th>Firstname</th>
    <th>Lastname</th>
    <th>Age</th>
    <th>Email</th>
    <th>Phone.No</th>
    </tr>
    </thead>
    <tbody>
    {state.data.map((data,index)=> (
        <tr key={index}>
        <td>{data.firstname}</td>
        <td>{data.lastname}</td>
        <td>{data.age}</td>
        <td>{data.email}</td>
        <td>{data.phoneno}</td>
        </tr>
    ))
        }
    </tbody>
    </table>


    </div>
  )
}

export default App
