import React,{useState} from 'react'

function App (){
  const[data,setData]=useState([])
 const[form,setForm]=useState({fname: '' ,lname: ' '})
  function getdata(e){
    e.preventDefault();
    const { nodename, name, value } = e.target;
    if (nodename === 'INPUT') {
      setForm({...setfName, [name]: value })
      setForm({...setlName, [name]: value })
    } 
  }
  function addIn(){

    setData([...data,form]);

    setForm({ fname: '', lname: '' });
  }
  return (
    <div className='App'>
    <form onChange={getdata}>
    <input name='fname' type="text" placeholder='enter your Firstname' onChange={setfName} value={form.fname}/><br /><br />
    <input name='lname'  type="text" placeholder='enter your Lastname' onChange={setlName}  value={form.lname}/><br /><br />

   <br /><br />


     <br />
     <br />
     <button onClick={addIn}>Submit</button>
     </form>

     <Table data={data} /> 
    </div>
  )
}
function Table({ data }) {


  function buildRows(data) {
    return data.map(obj => {
      return (
        <tr key={obj.fname}>
          <td>{obj.fname}</td>
          <td>{obj.lname}</td>
        </tr>
      );
    });
  }

  if (!data.length) return <div />;

  return (
    <table>
      <tbody>
        <tr className="heading">
          <td>Firstname</td>
          <td>Lastname</td>
        </tr>
        {buildRows(data)}
      </tbody>
    </table>
  );
}
 export default App;
