import React,{useState} from 'react'

function App (){
  const[data,setData]=useState([])
  const[fname,setFName]=useState({fname: ''})
  const[lname,setLName]=useState({lname: ''})

  function getdata(e){
    const { nodeName, name, value } = e.target;
    if (nodeName === 'INPUT') {
      setFName({...fname, [name]: value })
      setLName({...lname, [name]: value })
    }
    e.preventDefault();

  }
  function addIn(){
    setData([...data,fname,lname]);
    setFName({fname: ''})
    setLName({lname: ''})
  }
  return (
    <div className='App'>
    <form onSubmit={getdata}>
    <input name="fname" type="text" placeholder='enter your Firstname' onChange={(e)=>setFName(e.target.value)}/><br /><br />
    <input name="lname"  type="text" placeholder='enter your Lastname' onChange={(e)=>setLName(e.target.value)}/><br /><br />

   <br /><br />


     <br />
     <br />
     <button onClick={addIn}>Submit</button>
     </form>

     <Table data={data} />

    </div>
  )
}
function Table({ data }) {


  function buildRows(data) {
    return data.map(obj => {
      return (
        <tr key={obj.fname}>
          <td>{obj.fname}</td>
          <td>{obj.lname}</td>
        </tr>
      );
    });
  }

  if (!data.length) return <div />;

  return (
    <table>
      <tbody>
        <tr className="heading">
          <td>Firstname</td>
          <td>Lastname</td>
        </tr>
        {buildRows(data)}
      </tbody>
    </table>
  );
}

export default App
