    </div>
  )
}
function Table({ data }) {


  function buildRows(data) {
    return data.map(obj => {
      return (
        <tr key={obj.fname}>
          <td>{obj.fname}</td>
          <td>{obj.lname}</td>
        </tr>
      );
    });
  }

  if (!data.length) return <div />;

  return (
    <table>
      <tbody>
        <tr className="heading">
          <td>Firstname</td>
          <td>Lastname</td>
        </tr>
        {buildRows(data)}
      </tbody>
    </table>
  );
}
 export default App;
