import React ,{ useState } from 'react'
function App(){
  const[toggle,setToggle]=useState(4)
  const Toggle=(val) =>{
    if (input == val){
      setToggle(toggle+1)
    }
    if(input == val){
      setToggle(toggle-1)
    }
  }
  return (
    <div>
    <button onClick={Toggle}>toggle</button>

    </div>
  )
}

export default App;
